INSERT INTO `reports` (`UUID`, `NAME`, `CODE`, `VIEW`) VALUES
('3d5c842a-a33e-21b4-5580-795b623a87c2', 'List of Users', 'SELECT substring_index(users.ADDR1, '' '', 1) as `NUM`, substring(users.ADDR1, length(substring_index(users.ADDR1, '' '', 1)) + 2, length(users.ADDR1)) as `STREET`,\nusers.ADDR1, users.LNAME, users.FNAME, users.PHONE, dogs.NAME, dog_breeds.BREED, dogs.DESCRIPTION, dogs.DATE_BIRTH, dogs.SEX, dogs.DATE_RABIESEXP\nFROM dog_breeds RIGHT JOIN ((roles INNER JOIN (users INNER JOIN user_roles ON users.UUID = user_roles.USER) ON roles.UUID = user_roles.ROLE) INNER JOIN (dogs INNER JOIN dog_users ON dogs.UUID = dog_users.DOG) ON users.UUID = dog_users.USER) ON dog_breeds.UUID = dogs.BREED\nORDER BY `STREET` ASC;', 'dog/report/reports/list_of_users.phtml'),
('c8fd707e-f9bb-aca4-fd7c-8a4b2e191079', 'Delinquent Accounts', 'SELECT dogs.UUID, dogs.NAME, users.FNAME, users.LNAME, users.ADDR1, users.PHONE, dogs.DESCRIPTION, dogs.SEX, dogs.DATE_BIRTH, dogs.DATE_RABIESEXP, dog_breeds.BREED\r\nFROM (((dogs LEFT JOIN dog_licenses ON dogs.UUID = dog_licenses.DOG) INNER JOIN dog_users ON dogs.UUID = dog_users.DOG) INNER JOIN users ON dog_users.USER = users.UUID) LEFT JOIN dog_breeds ON dogs.BREED = dog_breeds.UUID\r\nWHERE (((dog_licenses.DOG) Is Null));', 'dog/report/reports/list_of_users.phtml');
